import React, { useState, useEffect } from 'react';
// import PropTypes from 'react-dom';
import StarWarsContext from './StarWarsContext';
import fetchPlanets from '../services/fetchPlanetsAPI';

function Provider(props) {
  const [responseAPI, setResponseAPI] = useState({});
useEffect(() => {
  setResponseAPI(fetchPlanets('https://swapi-trybe.herokuapp.com/api/planets/?page=1&format=api'));
}, []);
  const { children } = props;
  return <StarWarsContext.Provider>{children}</StarWarsContext.Provider>;
}

