import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import StarWarsContext from './StarWarsContext';
import fetchPlanets from '../services/fetchPlanetsAPI';

function Provider(props) {
  const [data, setData] = useState([{ teste: 'só testando' }]);
  const [textInputValue, setTextInputValue] = useState('');
  const [columnValue, setColumnValue] = useState('population');
  const [comparisonValue, setComparisonValue] = useState('maior que');
  const [numberValue, setNumberValue] = useState(0);
  const [filteredData, setFilteredData] = useState([]);
  const [filters, setFilters] = useState({
    filters: {
      filterByName: {
        name: '',
      },
      filterByNumericValues: [
        {
          column: columnValue,
          comparison: comparisonValue,
          value: numberValue,
        }],
    },
  });

  useEffect(() => {
    fetchPlanets().then((response) => setData(response.results));
  }, []);

  useEffect(() => {
    setFilteredData(data);
  }, [data]);

  const filterByName = (value) => {
    const arrayToFilter = [...data];
    const filteredArray = arrayToFilter.filter((item) => (
      item.name.toLowerCase().includes(value)
    ));
    setFilteredData(filteredArray);
  };

  const handleTextChange = (event) => {
    const { target } = event;
    const { value } = target;
    setTextInputValue(value);
    setFilters({
      ...filters,
      filterByName: {
        name: value,
      },
    });
    filterByName(value.toLowerCase());
  };

  const handleColumnChange = (event) => {
    const { target } = event;
    const { value } = target;
    console.log(value);
    setColumnValue(value);
    console.log(columnValue);
  };

  const handleComparisonChange = (event) => {
    const { target } = event;
    const { value } = target;
    setComparisonValue(value);
  };

  const handleNumberChange = (event) => {
    const { target } = event;
    const { value } = target;
    setNumberValue(value);
  };

  const filterByNumber = () => {
    const arrayToFilter = [...data];
    const { filterByNumericValues } = filters;
    const { column, comparisonValue, value } = filterByNumericValues[0];
    console.log(column);
    console.log(comparisonValue);
    console.log(value);
    // const filteredArray = arrayToFilter.filter((item) => (
    //   item.name.toLowerCase().includes(value)
    // ));
    // setFilteredData(filteredArray);
    console.log(filters);
  };

  const handleFilterClick = () => {
    setFilters({
      filterByNumericValues: [
        {
          column: columnValue,
          comparison: comparisonValue,
          value: numberValue,
        }],
    });
    filterByNumber();
  };

  const context = {
    data,
    filters,
    textInputValue,
    filteredData,
    columnValue,
    comparisonValue,
    numberValue,
    handleColumnChange,
    handleComparisonChange,
    handleNumberChange,
    handleFilterClick,
    handleTextChange,
  };
  const { children } = props;
  return (
    <StarWarsContext.Provider value={ context }>
      {children}
    </StarWarsContext.Provider>
  );
}

Provider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default Provider;
